#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir//ust/bin/env$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir//ust/bin/env$exe" node "$basedir/../node-js-simple-calculator/index.js" $args
  } else {
    & "$basedir//ust/bin/env$exe" node "$basedir/../node-js-simple-calculator/index.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "/ust/bin/env$exe" node "$basedir/../node-js-simple-calculator/index.js" $args
  } else {
    & "/ust/bin/env$exe" node "$basedir/../node-js-simple-calculator/index.js" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
